// client/src/components/Customers.js
import { useState, useEffect } from 'react';
import axios from 'axios';

export default function Customers() {
  const [customers, setCustomers] = useState([]);
  const [name, setName]           = useState('');
  const [email, setEmail]         = useState('');
  const [phone, setPhone]         = useState('');
  const [error, setError]         = useState('');

  // Fetch existing customers on mount
  useEffect(() => {
    axios.get('/api/customers', { withCredentials: true })
      .then(res => setCustomers(res.data))
      .catch(() => setError('Failed to load customers'));
  }, []);

  // Handle new customer submission
  const handleAdd = async e => {
    e.preventDefault();
    try {
      const res = await axios.post(
        '/api/customers',
        { name, email, phone },
        { withCredentials: true }
      );
      setCustomers(prev => [...prev, res.data]);
      setName(''); setEmail(''); setPhone(''); setError('');
    } catch (err) {
      setError(err.response?.data.error || 'Add customer failed');
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="card shadow-sm auth-card">
        <div className="card-body">
          <h2 className="text-center auth-title">Customers</h2>

          {error && (
            <div className="alert alert-danger auth-error">
              {error}
            </div>
          )}

          <form onSubmit={handleAdd} className="mb-4">
            <div className="mb-2">
              <input
                type="text"
                className="form-control"
                placeholder="Name"
                value={name}
                onChange={e => setName(e.target.value)}
                required
              />
            </div>
            <div className="mb-2">
              <input
                type="email"
                className="form-control"
                placeholder="Email"
                value={email}
                onChange={e => setEmail(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <input
                type="tel"
                className="form-control"
                placeholder="Phone (optional)"
                value={phone}
                onChange={e => setPhone(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-success w-100">
              Add Customer
            </button>
          </form>

          <ul className="list-group">
            {customers.map(c => (
              <li key={c.id} className="list-group-item">
                <strong>{c.name}</strong>
                <br />
                <small>{c.email}{c.phone ? ` • ${c.phone}` : ''}</small>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
