// client/src/components/PrivateRoute.js
import { useState, useEffect } from 'react';
import axios from 'axios';
import { Outlet, Link } from 'react-router-dom';

export default function PrivateRoute() {
  const [loggedIn, setLoggedIn] = useState(null);

  useEffect(() => {
    axios
      .get('/api/check-auth', { withCredentials: true })
      .then(res => setLoggedIn(res.data.loggedIn))
      .catch(() => setLoggedIn(false));
  }, []);

  // still checking → render nothing (or a spinner)
  if (loggedIn === null) return null;

  // not logged in → show message
  if (!loggedIn) {
    return (
      <div className="text-center mt-5">
        <p>
          You must <Link to="/login">log in</Link> to view this page.
        </p>
      </div>
    );
  }

  // authorized → render nested routes
  return <Outlet />;
}
