import { useState, useEffect } from 'react';
import axios from 'axios';

export default function CryptoWallets() {
  const [customers, setCustomers]       = useState([]);
  const [selectedCust, setSelectedCust] = useState(null);
  const [wallets, setWallets]           = useState([]);
  const [walletName, setWalletName]     = useState('');
  const [balance, setBalance]           = useState('');
  const [error, setError]               = useState('');

  // 1. Fetch customers
  useEffect(() => {
    axios.get('/api/customers', { withCredentials: true })
      .then(res => setCustomers(res.data))
      .catch(() => setError('Failed to load customers'));
  }, []);

  // 2. Fetch wallets when a customer is selected
  useEffect(() => {
    if (!selectedCust) return;
    axios.get(`/api/customers/${selectedCust.id}/wallets`, { withCredentials: true })
      .then(res => setWallets(res.data))
      .catch(() => setError('Failed to load wallets'));
  }, [selectedCust]);

  // 3. Handle wallet creation
  const handleAddWallet = async e => {
    e.preventDefault();
    try {
      const res = await axios.post(
        '/api/wallets',
        {
          wallet_name: walletName,
          balance: parseFloat(balance),
          customer_id: selectedCust.id
        },
        { withCredentials: true }
      );
      setWallets(prev => [...prev, res.data]);
      setWalletName('');
      setBalance('');
      setError('');
    } catch (err) {
      setError(err.response?.data.error || 'Add wallet failed');
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="card shadow-sm auth-card" style={{ maxWidth: 600 }}>
        <div className="card-body">
          <h2 className="text-center auth-title">Crypto Wallets</h2>

          {error && (
            <div className="alert alert-danger auth-error">
              {error}
            </div>
          )}

          {/* Customer Buttons */}
          <div className="d-flex flex-wrap mb-4">
            {customers.map(c => (
              <button
                key={c.id}
                className={`btn btn-outline-primary me-2 mb-2 ${
                  selectedCust?.id === c.id ? 'active' : ''
                }`}
                onClick={() => setSelectedCust(c)}
              >
                {c.name}
              </button>
            ))}
          </div>

          {/* If a customer is selected, show wallets and add form */}
          {selectedCust && (
            <>
              <h5>Wallets for {selectedCust.name}:</h5>
              <ul className="list-group mb-4">
                {wallets.map(w => (
                  <li key={w.id} className="list-group-item">
                    {w.wallet_name} — Balance: {w.balance.toFixed(4)}
                  </li>
                ))}
                {wallets.length === 0 && (
                  <li className="list-group-item text-muted">
                    No wallets yet.
                  </li>
                )}
              </ul>

              <form onSubmit={handleAddWallet}>
                <div className="mb-2">
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Wallet Name"
                    value={walletName}
                    onChange={e => setWalletName(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <input
                    type="number"
                    step="0.0001"
                    className="form-control"
                    placeholder="Initial Balance"
                    value={balance}
                    onChange={e => setBalance(e.target.value)}
                    required
                  />
                </div>
                <button type="submit" className="btn btn-success w-100">
                  Add Wallet
                </button>
              </form>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
